import React from 'react';
import { Row, Col, Image, Button } from 'antd';
import { useAppState } from "./AppStateContext";
import { fetchFromFile, createProject } from './utils/dataRetrieve';
import { useHistory } from 'react-router-dom';
// import {
//     Link
// } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';

interface report {
    direct: number,
    inherited: number,
    transitive: number
}
interface project {
    tittle: string,
    id: number,
    version: string,
    cleanURL: string,
    gitURL: string,
    img: string,
    normalReport: report,
    depCleanReport: report,
    description: string,
}

interface projectProps {
    data: project
}

//Interface for an artifact in the POM XML
interface artifact {
    coordinates: string,
    groupId: string,
    artifactId: string,
    version: string,
    scope: "compile" | "provided" | "runtime" | "test" | "sytem" | "import" | "null",
    packaging: "jar" | "war"
    omitted: boolean,
    classifier: string,
    parent: string,
    size: number,
    status: "used" | "bloated"
    type: "parent" | "direct" | "omitted" | "transitive" | "inherited"
    children: artifact[],
    highlight: boolean,
    visible: boolean,
}




export const Project = ({ data }: React.PropsWithChildren<projectProps>) => {
    let history = useHistory();

    const { dispatch } = useAppState();

    const handleClick = async (url: string) => {
        await fetchFromFile(url)
            .then((data) => {
                const project: artifact = createProject(data);
                dispatch({ type: "LOAD_LOCAL_FILE", payload: project });
                history.push("/");
            })
            .catch((err) => {
                console.log("error", err)
            });
    }

    return (
        <Row gutter={[20, 20]} key={uuidv4()} >
            <Col span="16" offset={4} className="flex margin-xs boder-round shadow-xs project">
                <Image
                    src="error"
                    fallback="data:image/png;base64,"
                />
                <div style={{ width: "100%" }}>
                    <div className="gal-tittle">
                        <div className="flex flex-base" >
                            <h3>{data.tittle}</h3>
                            <span>{data.version}</span>
                        </div>
                        <p>{data.description}</p>
                        {/* <a href={data.gitURL} target="_blank">Git</a> */}
                    </div>
                    <div className="flex description">
                        <Dependencies dataUsed={data.normalReport} dataBloated={data.depCleanReport} key={uuidv4()} />
                        <div className="flex flex-vertical center">
                            <Button
                                className="button btn-green"
                                type="primary"
                                size="large"
                                onClick={() => handleClick(data.cleanURL)}
                            >
                                DepClean Project
                            </Button>
                            <a href={data.gitURL} target="_blank">View GitHub</a>
                        </div>
                    </div>

                </div>

            </Col>
        </Row>
    );
}

interface dependenciesProps {
    dataUsed: report
    dataBloated: report
}

const Dependencies = ({ dataUsed, dataBloated }: React.PropsWithChildren<dependenciesProps>) => {

    return (
        <div key={uuidv4()} className="width-90">
            <table className="table-report">
                <thead>
                    <tr>
                        <th>Dependencies</th>
                        <th>Direct</th>
                        <th>Transitive</th>
                        <th>Inherited</th>
                    </tr>
                </thead>
                <tbody>

                    <tr>
                        <td>Used</td>
                        <td>{dataUsed.direct}</td>
                        <td>{dataUsed.inherited}</td>
                        <td>{dataUsed.transitive}</td>
                    </tr>
                    <tr>
                        <td>Bloated</td>
                        <td className="highlight-red">{dataBloated.direct}</td>
                        <td className="highlight-red">{dataBloated.inherited}</td>
                        <td className="highlight-red">{dataBloated.transitive}</td>
                    </tr>
                </tbody>
            </table>
        </div>
    )
}